import React, {useState, useEffect } from 'react';
import { Outlet, Link } from 'react-router-dom';

import Navbar from "../Utilites/Navbar";

// Step 1
const Homepage = () => {
    const [posts, setPosts] = useState([])

// Use Effects
    // URL to make template literal
    const apiBaseURL = "https://strangers-things.herokuapp.com/api/2209-ftb-mt-web-ft";

    // Fetching Post; Step 2a
    useEffect(() => {
        async function fetchThingsData() {
            try {
                const response = await fetch(`${apiBaseURL}/posts`);
                const results = await response.json();
                // console.log(results);
                setPosts(results.data.posts);
            } catch (error) {
                console.log(error);
            }
        }
        fetchThingsData();

    }, [])

    return (
        <div>
            <div className="header">
                <h1 className="title">Stranger's Things</h1>
                <Navbar />
            </div>


            <Outlet context={posts}/>

        </div>

    )
}

export default Homepage;
