import React, { useState } from 'react';
import { useOutletContext, useNavigate } from "react-router-dom";

// URL to make template literal
    // I could just save this in an API folder & import it everytime, i'll get to it eventually
const apiBaseURL = "https://strangers-things.herokuapp.com/api/2209-ftb-mt-web-ft";

// FN
const NewPosts = () => {
    const [token, setCreateNewPost] = [props.token, props.setCreateNewPost];
    const [deliveryCheck, setDeliveryCheck] = useState(false);
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");

    return (
      <div className="createPost">
        {/*Create Post Form*/}
        <form
          onSubmit={async(event) => {
            {/*API Call, Create New Post*/}
            event.preventDefault();
            const postObj = {
              title: title,
              description: description,
              price: price,
              willDeliver: deliveryCheck,
            };
            await createPost(postObj, token);
            setCreateNewPost(false);
          }}
        > {/*Create Post Text Boxes*/}
          <div className="createInputs">
            <label htmlFor="title">Title</label>
          <fieldset className="createfieldset">
            <input
            size={'58'}
              minLength={1}
              id="title"
              type="text"
              placeholder="Enter Title"
              value={title}
              onChange={(event) => {
                event.preventDefault();
                setTitle(event.target.value);
              }}
            />
          </fieldset>
            <label htmlFor="description">Description</label>
          <fieldset className="createfieldset" >
            <textarea
            rows={'5'}
            cols="50"
              minLength={1}
              id="description"
              type="text"
              placeholder="Enter Description"
              value={description}
              onChange={(event) => {
                event.preventDefault();
                setDescription(event.target.value);
              }}
            />
          </fieldset>
            <label htmlFor="price">Price</label>
          <fieldset className="createfieldset" >
            <input
            size={'58'}
              minLength={1}
              id="price"
              type="text"
              placeholder="Set Price"
              value={price}
              onChange={(event) => {
                event.preventDefault();
                setPrice(event.target.value);
              }}
            />
          </fieldset>
          {/*Will Deliver Checkbox*/}
          <fieldset className="createfieldset">
            <label  htmlFor="willDeliver">Will You Deliver?</label>
            <input className="checkbox"
              id="willDeliver"
              type="checkbox"
              onChange={(event) => {
                setDeliveryCheck(!deliveryCheck);
              }}
            />
          </fieldset>
          </div>
          {/*Buttons*/}
          <button className="createPostButton" type="submit">Create Post</button>
          <button className="cancelButton"
            onClick={() => {
              setCreateNewPost(false);
            }}
          >
            Cancel
          </button>
        </form>
      </div>
    );
  };

export default NewPosts;
